import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";

function App() {
    return (
        <div className="container">
            <Header />
            <Menu />
            <Footer />
        </div>
    );
}

function Menu() {
    return (
        <main className="menu">
            <h2>Our Menu</h2>
            <Pizza
                name="Focaccia"
                ingredients="Bread with italian olive oil and rosemary"
                photoName="pizzas/focaccia.jpg"
                price="6"
                soldOut="false"
            />
            <Pizza
                name="Pizza Margherita"
                ingredients="Tomato and mozarella"
                photoName="pizzas/margherita.jpg"
                price="10"
                soldOut="false"
            />
            <Pizza
                name="Pizza Spinaci"
                ingredients="Tomato, mozarella, spinach, and ricotta cheese"
                photoName="pizzas/spinaci.jpg"
                price="12"
                soldOut="false"
            />
            <Pizza
                name="Pizza Funghi"
                ingredients="Tomato, mozarella, mushrooms, and onion"
                photoName="pizzas/funghi.jpg"
                price="12"
                soldOut="false"
            />
            <Pizza
                name="Pizza Salamino"
                ingredients="Tomato, mozarella, and pepperoni"
                photoName="pizzas/salamino.jpg"
                price="15"
                soldOut="true"
            />
            <Pizza
                name="Pizza Prosciutto"
                ingredients="Tomato, mozarella, ham, aragula, and burrata cheese"
                photoName="pizzas/prosciutto.jpg"
                price="18"
                soldOut="false"
            />
            <Pizza
                name="Pizza Capricciosa"
                ingredients="Tomato, mozarella, artichokes, mushrooms, and onion"
                photoName="pizzas/capricciosa.jpg"
                price="20"
                soldOut="false"
            />
        </main>
    );
}

function Pizza(props) {
    console.log(props);
    if (props.soldOut === "false") {
        return null;
    }
    return (
        <div>
            <img src={props.photoName} alt={props.name} />
            <h3 style={{ color: "black" }}>{props.name}</h3>
            <p>{props.ingredients}</p>
        </div>
    );
}

function Header() {
    return (
        <header className="header">
            <h1>Fast React Pizza Company.</h1>
        </header>
    );
}

function Footer() {
    let isOpen = new Date().getHours();
    let isAM = isOpen < 12;
    let hours12Format = isOpen % 12 || 12;
    let currentTime = `${hours12Format}:${new Date().getMinutes()} ${
        isAM ? "AM" : "PM"
    }`;

    console.log(currentTime);
    isOpen = currentTime > "11:00" ? "Closed" : "Open";
    return (
        <footer className="footer">
            <b>{currentTime}</b> we're currently {isOpen}
        </footer>
    );
}

const root = ReactDOM.createRoot(document.querySelector("#root"));
root.render(
    <React.StrictMode>
        <App />
    </React.StrictMode>
);

