function Pizza(props) {
    // console.log(props);

    let checkSold = props.pizzaObjb.soldOut ? "soldOut" : "";

    return (
        <div className={"pizza " + checkSold}>
            <img src={props.pizzaObjb.photoName} alt={props.pizzaObjb.name} />
            <div>
                <h3 style={{ color: "black" }}>{props.pizzaObjb.name}</h3>
                <p>{props.pizzaObjb.ingredients}</p>
                <span>{props.pizzaObjb.price + 3 + "€"}</span>
            </div>
        </div>
    );
}

function Header() {
    return (
        <header className="header">
            <h1>Fast React Pizza Company.</h1>
        </header>
    );
}

function Footer() {
    let isOpen = new Date().getHours();
    let isAM = isOpen < 12;
    let hours12Format = isOpen % 12 || 12;
    let closeHore = 12;
    let currentTime = `${hours12Format}:${new Date().getMinutes()} ${
        isAM ? "AM" : "PM"
    }`;

    console.log(currentTime);
    isOpen = currentTime > "11:00" ? "Closed" : "Open";
    return (
        <footer className="footer">
            {isOpen && (
                <div>
